mapper.insert:
INSERT INTO wz_orders (
    order_id, user_id, total_amount, status,
    created_time, expire_time, version, created_user
) VALUES (
    #{orderId}, #{userId}, #{totalAmount}, #{status},
    #{createdTime}, #{expireTime}, #{version}, #{createdUser}
)

mapper.select:
SELECT * FROM wz_orders 
WHERE user_id = #{userId}
ORDER BY created_time DESC

mapper.update:
UPDATE wz_orders 
SET status = #{newStatus},
    version = version + 1
WHERE order_id = #{orderId} 
AND status = #{oldStatus}
AND version = #{version}

UPDATE wz_orders 
SET status = #{status},
    pay_time = #{payTime},
    payment_id = #{paymentId},
    modified_time = #{modifiedTime},
    modified_user = #{modifiedUser},
    version = version + 1
WHERE order_id = #{orderId} 
AND version = #{version}
AND status = #{oldStatus}

mapper.select:
SELECT * FROM wz_orders 
WHERE status = 'PENDING_PAY'
AND expire_time < #{currentTime}
