mapper.select:
SELECT p.*, r.review_id, r.user_id, r.rating as review_rating, r.content, r.created_time as review_created_time, u.username FROM wz_products p LEFT JOIN (
SELECT * FROM ( SELECT pr.*, ROW_NUMBER() OVER (PARTITION BY product_id ORDER BY created_time DESC) as rn FROM wz_product_reviews pr ) ranked WHERE ranked.rn <= 5
) r ON p.product_id = r.product_id LEFT JOIN wz_users u ON r.user_id = u.uid WHERE p.is_active = 1
AND p.category_id = #{categoryId}
AND ( p.name LIKE CONCAT('%', #{keyword}, '%') OR p.description LIKE CONCAT('%', #{keyword}, '%') OR p.brand LIKE CONCAT('%', #{keyword}, '%') OR p.tags LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY p.created_time DESC, r.created_time DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM wz_products p WHERE p.is_active = 1
AND p.category_id = #{categoryId}
AND ( p.name LIKE CONCAT('%', #{keyword}, '%') OR p.description LIKE CONCAT('%', #{keyword}, '%') OR p.brand LIKE CONCAT('%', #{keyword}, '%') OR p.tags LIKE CONCAT('%', #{keyword}, '%') )

SELECT p.*
FROM wz_products p
WHERE p.product_id = #{productId}
